
/*
    Current HL7 Standard Code Set
    Vaccines Administered in the United States
    http://www2a.cdc.gov/vaccines/iis/iisstandards/vaccines.asp?rpt=cvx
*/

CREATE TABLE VaccinationKind (
    VaccinationKindID int          PRIMARY KEY,
    CVXCode           int          NOT NULL,
    Status            varchar(20)  NOT NULL,
    DateUpdated       date         NOT NULL,
    VaccinationName   varchar(100) NOT NULL,
    VaccinationDesc   varchar(250) NOT NULL,
    Notes             varchar(500)
);

